INSERT INTO `categories` (`id`, `name`, `description`, `color`, `status`, `is_featured`, `deleted_at`, `created_at`, `updated_at`) VALUES
(9, 'Vệ sinh máy lạnh', 'Trải nghiệm sự thoải mái hơn với dịch vụ Vệ sinh máy lạnh của chúng tôi, đảm bảo hiệu suất làm mát tối ưu cho không gian của bạn.🔩 🪛', '#000000', 1, 1, NULL, '2023-09-04 05:55:09', '2023-09-04 05:55:15'),
(10, 'May vá', 'Chuyên mục Thợ may hướng dẫn bạn về nghệ thuật xử lý vải và may trang phục, từ những kỹ thuật may cơ bản đến các kỹ thuật nâng cao, giúp bạn tạo ra những sản phẩm được may đo hoàn hảo. 🧥🥻👔', '#000000', 1, 0, NULL, '2023-09-04 05:56:14', '2023-09-04 05:56:14'),
(11, 'Nhà thông minh', 'Danh mục Nhà thông minh đi sâu vào lĩnh vực thiết bị kết nối và tự động hóa, biến không gian sống thành môi trường hiệu quả và thông minh về công nghệ. 📱', '#000000', 1, 0, NULL, '2023-09-04 05:57:10', '2023-09-04 05:57:10'),
(12, 'An ninh', 'Đảm bảo an toàn và yên tâm với dịch vụ An ninh của chúng tôi, cung cấp sự bảo vệ thận trọng cho cơ sở của bạn.👮🏻', '#000000', 1, 0, NULL, '2023-09-04 05:57:44', '2023-09-04 05:57:44'),
(13, 'Vệ sinh nhà cửa', 'Danh mục Vệ sinh cung cấp hướng dẫn chuyên môn về việc duy trì môi trường sạch sẽ và hợp vệ sinh thông qua các biện pháp vệ sinh và khử trùng hiệu quả.🧴', '#000000', 1, 0, NULL, '2023-09-04 05:58:07', '2025-04-18 10:36:11'),
(14, 'Salon', 'Danh mục Salon là nơi cung cấp các chuyên gia về làm đẹp và chải chuốt, cung cấp thông tin chuyên sâu về tạo mẫu tóc, chăm sóc da và thói quen chăm sóc cá nhân để bạn luôn tự tin và chỉn chu. ✂️', '#000000', 1, 0, NULL, '2023-09-04 05:58:27', '2023-09-04 05:58:27'),
(15, 'Nhiếp ảnh ', 'Thể loại Nhiếp ảnh nắm bắt được bản chất của việc kể chuyện bằng hình ảnh, cung cấp các mẹo về bố cục, chỉnh sửa và thiết bị để giúp bạn tạo ra những hình ảnh hấp dẫn. 📸', '#000000', 1, 0, NULL, '2023-09-04 05:58:53', '2023-09-04 05:58:53'),
(16, 'Kiểm soát động vật gây hại', 'Danh mục Kiểm soát dịch hại cung cấp cho bạn các chiến lược hiệu quả để quản lý và loại bỏ dịch hại, đảm bảo môi trường không có dịch hại và sự an tâm. 🪲🪳', '#000000', 1, 0, NULL, '2023-09-04 05:59:13', '2023-09-04 05:59:13'),
(17, 'Thần số học', 'Thể loại Thần số học cung cấp hướng dẫn tâm linh, nghi lễ và hiểu biết sâu sắc bắt nguồn từ các truyền thống cổ xưa, hỗ trợ mọi người trong hành trình hướng tới sự hiểu biết sâu sắc hơn và kết nối.🔥', '#000000', 1, 0, NULL, '2023-09-04 05:59:31', '2023-09-04 05:59:31'),
(18, 'Sơn tường', 'Danh mục Sơn tường mang đến không gian sống động và đầy màu sắc, biến những bức tường đơn điệu thành tác phẩm nghệ thuật. Chúng tôi cung cấp các giải pháp sơn trang trí với đa dạng phong cách, kỹ thuật và chất liệu, phù hợp cho cả không gian cá nhân và thương mại. 🎨🖌️', '#000000', 1, 1, NULL, '2023-09-04 06:00:04', '2025-04-18 10:34:53'),
(19, 'Giặc ủi', 'Trải nghiệm sự tiện lợi của quần áo sạch sẽ với dịch vụ Giặt ủi toàn diện của chúng tôi, đảm bảo quần áo của bạn luôn tươi mới và được chăm sóc chu đáo.🧼', '#000000', 1, 0, NULL, '2023-09-04 06:00:26', '2023-09-04 06:00:26'),
(20, 'Làm vườn', 'Thể loại Người làm vườn cung cấp nhiều kiến thức về chăm sóc cây trồng, cảnh quan và các hoạt động làm vườn bền vững để giúp những người đam mê nuôi dưỡng không gian xanh tươi tốt. 🏡⛏️', '#000000', 1, 0, NULL, '2023-09-04 06:00:55', '2025-02-28 02:16:19'),
(21, 'Chăm sóc ô tô', 'Trải nghiệm dịch vụ chăm sóc ô tô hàng đầu cho độ bền và hiệu suất của xe bạn. Từ bảo dưỡng đến sửa chữa, các chuyên gia của chúng tôi đảm bảo xe của bạn luôn ở trạng thái tốt nhất. 🚛🚙', '#000000', 1, 0, NULL, '2023-09-04 06:01:27', '2023-09-04 06:01:27'),
(22, 'Nấu ăn', 'Danh mục Nấu ăn cung cấp một cuộc khám phá thú vị về các kỹ thuật nấu ăn, công thức nấu ăn và mẹo nấu ăn, phục vụ cho cả người mới bắt đầu nấu ăn và đầu bếp dày dạn kinh nghiệm. 🫕🍲', '#000000', 1, 1, NULL, '2023-09-04 06:02:07', '2025-04-18 10:36:20'),
(23, 'Sửa ống nước', 'Danh mục Thợ sửa ống nước hướng dẫn bạn tìm hiểu về các đường ống, đồ đạc và hệ thống ống nước phức tạp để giúp bạn giải quyết các vấn đề và thành thạo các kỹ năng sửa ống nước cần thiết. 🛠️', '#000000', 1, 1, NULL, '2023-09-04 06:02:54', '2023-09-04 06:02:54'),
(24, 'Vệ sinh', 'Loại bỏ bụi bẩn và cặn bẩn hiệu quả bằng các sản phẩm vệ sinh của chúng tôi, khôi phục bề mặt về tình trạng nguyên sơ. 🧼🧽🧹🧻', '#000000', 1, 0, NULL, '2023-09-04 06:03:21', '2025-04-18 10:36:28'),
(25, 'Sửa điện', 'Khám phá danh mục Thợ điện để hiểu rõ hơn về hệ thống điện, từ sự phức tạp của hệ thống dây điện đến các yếu tố cần thiết về an toàn. 💡🪛🔌', '#000000', 1, 0, NULL, '2023-09-04 06:04:01', '2025-04-18 10:36:14'),
(26, 'Thợ mộc', 'Thợ mộc là một người thợ lành nghề chuyên làm việc với gỗ để xây dựng, lắp đặt và sửa chữa các công trình và đồ vật. 🪛 🪚', '#000000', 1, 0, NULL, '2023-09-04 06:04:41', '2025-02-28 02:16:13'),
(27, 'Dịch vụ từ xa', 'Dịch vụ này sẽ được hoàn thành Trực tuyến/Từ xa. 🧑🏻‍🔧🔧🪛', '#000000', 1, 0, NULL, '2023-09-04 06:05:01', '2025-04-18 10:36:16');
