(12, 'Lắp đặt máy lạnh cửa sổ', 9, 16, 300000, 'hourly', '00:30', 2, 1, 'Tận hưởng sự mát mẻ không rắc rối với dịch vụ lắp đặt Window AC của chúng tôi. Các chuyên gia của chúng tôi cung cấp sự vừa vặn và độ kín tối ưu, đảm bảo thiết bị của bạn được lắp đặt an toàn và hoạt động hiệu quả.', 0, 1, NULL, '2023-09-04 06:31:06', '2023-09-04 06:31:06', 1, 'service', 0, 0, NULL, 'on_site'),
(13, 'Thay dầu và kiểm tra chất lỏng', 21, 16, 120000, 'fixed', '00:30', 1, 1, 'Đảm bảo sức khỏe động cơ. Các chuyên gia của chúng tôi thực hiện thay dầu và kiểm tra chất lỏng thường xuyên để duy trì hiệu suất cao nhất.', 1, 1, NULL, '2023-09-04 06:33:12', '2023-09-04 06:33:12', 3, 'service', 0, 0, NULL, 'on_site'),
(14, 'Luân chuyển và cân bằng lốp', 21, 15, 230000, 'fixed', '00:30', 2, 1, 'Tối đa hóa tuổi thọ lốp xe. Chúng tôi xoay và cân bằng lốp xe để cân bằng độ mòn và đảm bảo xe chạy êm ái, an toàn.', 0, 1, NULL, '2023-09-04 06:34:45', '2023-09-05 00:25:14', 3, 'service', 0, 1, 10, 'on_site'),
